mapper.select:
select userid
from tbl_member
where userid = #{userid}

select email
from tbl_member
where email = #{email}

select userid, user_name
from tbl_member
where user_name = #{name} and email = #{email}

select companyid, company_name
from tbl_company
where company_name = #{name} and email = #{email}

select userid
from tbl_member
where userid = #{id} and email = #{email}

select companyid
from tbl_company
where companyid = #{id} and email = #{email}

select pw
from tbl_member
where userid = #{id} and pw = #{new_pw}

select pw
from tbl_company
where companyid = #{id} and pw = #{new_pw}

SELECT seq, fk_userid, name, subject, readCount, regDate, commentCount , filename, orgfilename, filesize FROM ( select row_number() over(order by CASE WHEN fk_userid = 'admin' THEN 0 ELSE 1 END, seq desc) AS rno , seq, fk_userid, name, subject , readCount, to_char(regDate, 'yyyy-mm-dd hh24:mi') AS regDate , commentCount, filename, orgfilename, filesize from tbl_board where status = 1 and category = #{category}
and lower(subject) like '%' || lower(#{searchWord}) || '%'
and lower(content) like '%' || lower(#{searchWord}) || '%'
and lower(name) like '%' || lower(#{searchWord}) || '%'
) V WHERE V.rno between #{startRno} and #{endRno}

SELECT previousseq, previoussubject , seq, fk_userid, name, subject, content, readCount, regDate, pw , nextseq, nextsubject , commentcount, fileName, orgFilename, fileSize, category FROM ( select lag(seq, 1) over(order by seq desc) AS previousseq , lag(subject, 1) over(order by seq desc) AS previoussubject , seq, fk_userid, name, subject, content, readCount , to_char(regDate, 'yyyy-mm-dd hh24:mi') as regDate, pw , lead(seq, 1) over(order by seq desc) AS nextseq , lead(subject, 1) over(order by seq desc) AS nextsubject , commentcount, fileName, orgFilename, fileSize, category from tbl_board where status = 1 and category = #{category}
and lower(subject) like '%' || lower(#{searchWord}) || '%'
and lower(content) like '%' || lower(#{searchWord}) || '%'
and (lower(subject) like '%' || lower(#{searchWord}) || '%' or lower(content) like '%' || lower(#{searchWord}) || '%')
and lower(name) like '%' || lower(#{searchWord}) || '%'
) V WHERE V.seq = #{seq}

mapper.update:
update tbl_board
set readCount = readCount + 1
where seq = #{seq}

update tbl_board
set subject = #{subject}, content = #{content}, category = #{category}
where seq = #{seq} and fk_userid = #{fk_userid} and pw = #{pw}

update tbl_board
set subject = #{subject}, content = #{content}, category = #{category},
	filename = #{fileName}, orgfilename = #{orgFilename}, filesize = #{fileSize}
where seq = #{seq} and fk_userid = #{fk_userid} and pw = #{pw}

mapper.select:
select seq, fk_userid, name, content, regdate, parentseq
from tbl_comment
where status = 1 and seq = #{seq}

select commentCount
from tbl_board
where seq = #{seq}

select food_store_code
from tbl_food_store
where (food_name like '%' || #{food_name} || '%') and local_status = #{local_status}
