mapper.select:
SELECT review_content,fk_userid,registerday,review_code,RNO
FROM
	(
		select review_content,fk_userid,registerday,review_code,rownum as RNO
		from tbl_review
		where parent_code = #{parent_code}
		order by registerday desc
	)
WHERE RNO between #{startRno} and #{endRno}

select count(*)
from tbl_review
where parent_code = #{parent_code}

mapper.update:
update tbl_play
play_category = #{play_category}, local_status = #{local_status}, play_name = #{play_name}, play_content = #{play_content}, play_mobile = #{play_mobile}, play_businesshours = #{play_businesshours}, play_address = #{play_address}
, fileName = #{fileName}, orgFilename = #{orgFilename}, fileSize = #{fileSize}, play_main_img = #{fileName}
where play_code = #{play_code}

mapper.select:
select fk_userid,parent_code,like_division_R
from tbl_like
where fk_userid = #{fk_userid} and parent_code = #{parent_code}

select count(*)
from tbl_like
where parent_code = #{parent_code}

select scheduleno, startdate, enddate, subject, color, place, joinuser, content, fk_smcatgono, fk_lgcatgono, fk_userid ,schedule_divison
from tbl_calendar_schedule
where fk_userid = #{fk_userid} and parent_code = #{parent_code}

select count(*)
from tbl_review
where fk_userid = #{fk_userid}

SELECT review_content,registerday,RNO ,review_division_R ,parent_code
FROM
	(
		select review_content,to_char(registerday,'yyyy-mm-dd')as registerday ,row_number() over(order by registerday desc) as rno , review_division_R ,parent_code
		from tbl_review
		where fk_userid = #{fk_userid}
		Order By registerday Desc
		
	)
WHERE RNO between #{startRno} and #{endRno}
